import React, { useState } from 'react';
import axios from 'axios';

/**
 * Simple Login/Register Component
 * Features:
 * - User Registration with username, email, password
 * - User Login with email and password
 * - Form validation
 * - Error handling
 * - JWT token management
 */
function SimpleLogin({ setToken, setUser }) {
  // State management for form
  const [isLogin, setIsLogin] = useState(true); // Toggle between login/register
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });
  const [message, setMessage] = useState(''); // Success/error messages
  const [loading, setLoading] = useState(false); // Loading state

  /**
   * Handle form submission for both login and register
   * @param {Event} e - Form submit event
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      // Determine API endpoint based on login/register mode
      const url = isLogin ? '/auth/login' : '/auth/register';
      
      // Prepare data - login only needs email/password, register needs all fields
      const data = isLogin
        ? { email: formData.email, password: formData.password }
        : formData;

      // Make API call to backend
      const response = await axios.post(`http://localhost:5055/api${url}`, data);

      // If successful, save token and user data
      if (response.data.token) {
        setToken(response.data.token); // Set token in parent component
        setUser(response.data.user); // Set user data in parent component
        localStorage.setItem('token', response.data.token); // Save token to localStorage
        setMessage(isLogin ? '✅ Login successful!' : '✅ Registration successful!');
      }
    } catch (error) {
      // Handle errors from backend
      const errorMessage = error.response?.data?.message || 'Something went wrong';
      setMessage('❌ ' + errorMessage);
    }
    setLoading(false);
  };

  /**
   * Handle input field changes
   * @param {Event} e - Input change event
   */
  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="login-container">
      <div className="login-card">
        {/* Header */}
        <div className="login-header">
          <h1>💰 ExpenseFlow</h1>
          <p>Smart expense tracking for modern teams</p>
        </div>

        {/* Tab Switcher */}
        <div className="tab-switcher">
          <button
            className={isLogin ? 'active' : ''}
            onClick={() => setIsLogin(true)}
          >
            Login
          </button>
          <button
            className={!isLogin ? 'active' : ''}
            onClick={() => setIsLogin(false)}
          >
            Register
          </button>
        </div>

        {/* Login/Register Form */}
        <form onSubmit={handleSubmit} className="login-form">
          {/* Username field - only show for registration */}
          {!isLogin && (
            <div className="form-group">
              <label>Full Name</label>
              <input
                type="text"
                name="username"
                placeholder="Enter your full name"
                value={formData.username}
                onChange={handleInputChange}
                required={!isLogin}
              />
            </div>
          )}

          {/* Email field - required for both login and register */}
          <div className="form-group">
            <label>Email Address</label>
            <input
              type="email"
              name="email"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>

          {/* Password field - required for both login and register */}
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              name="password"
              placeholder="Enter your password"
              value={formData.password}
              onChange={handleInputChange}
              required
            />
          </div>

          {/* Submit button */}
          <button
            type="submit"
            disabled={loading}
            className="submit-btn"
          >
            {loading ? 'Please wait...' : (isLogin ? 'Login' : 'Register')}
          </button>
        </form>

        {/* Success/Error Messages */}
        {message && (
          <div className={`message ${message.includes('✅') ? 'success' : 'error'}`}>
            {message}
          </div>
        )}
      </div>

      {/* CSS Styles */}
      <style jsx>{`
        .login-container {
          min-height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          font-family: 'Arial', sans-serif;
        }

        .login-card {
          background: white;
          padding: 2rem;
          border-radius: 10px;
          box-shadow: 0 10px 25px rgba(0,0,0,0.2);
          width: 100%;
          max-width: 400px;
        }

        .login-header {
          text-align: center;
          margin-bottom: 2rem;
        }

        .login-header h1 {
          color: #333;
          margin: 0 0 0.5rem 0;
          font-size: 2rem;
        }

        .login-header p {
          color: #666;
          margin: 0;
        }

        .tab-switcher {
          display: flex;
          margin-bottom: 2rem;
          border-radius: 5px;
          overflow: hidden;
          border: 1px solid #ddd;
        }

        .tab-switcher button {
          flex: 1;
          padding: 10px;
          border: none;
          background: #f5f5f5;
          cursor: pointer;
          transition: all 0.3s;
        }

        .tab-switcher button.active {
          background: #667eea;
          color: white;
        }

        .login-form {
          display: flex;
          flex-direction: column;
          gap: 1rem;
        }

        .form-group {
          display: flex;
          flex-direction: column;
        }

        .form-group label {
          margin-bottom: 0.5rem;
          color: #333;
          font-weight: bold;
        }

        .form-group input {
          padding: 12px;
          border: 1px solid #ddd;
          border-radius: 5px;
          font-size: 16px;
          transition: border-color 0.3s;
        }

        .form-group input:focus {
          outline: none;
          border-color: #667eea;
        }

        .submit-btn {
          padding: 12px;
          background: #667eea;
          color: white;
          border: none;
          border-radius: 5px;
          font-size: 16px;
          cursor: pointer;
          transition: background 0.3s;
          margin-top: 1rem;
        }

        .submit-btn:hover {
          background: #5a6fd8;
        }

        .submit-btn:disabled {
          background: #ccc;
          cursor: not-allowed;
        }

        .message {
          margin-top: 1rem;
          padding: 10px;
          border-radius: 5px;
          text-align: center;
        }

        .message.success {
          background: #d4edda;
          color: #155724;
          border: 1px solid #c3e6cb;
        }

        .message.error {
          background: #f8d7da;
          color: #721c24;
          border: 1px solid #f5c6cb;
        }
      `}</style>
    </div>
  );
}

export default SimpleLogin;
