// Import required React modules and components
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

// Import page components
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Groups from './pages/Groups';
import Expenses from './pages/Expenses';
import Analytics from './pages/Analytics';
import './App.css';

function App() {
  const [token, setToken] = useState(''); // Start with empty token
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Clear any existing token on app start
    localStorage.removeItem('token');
    setToken('');
    setUser(null);
  }, []); // Run only once on app start

  useEffect(() => {
    if (token) {
      // You can decode token to get user info if needed
      setUser({ email: 'test@example.com' }); // Placeholder
    }
  }, [token]);

  const logout = () => {
    setToken('');
    setUser(null);
    localStorage.removeItem('token');
  };

  return (
    <GoogleOAuthProvider clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID || "YOUR_GOOGLE_CLIENT_ID"}>
      <Router>
        <div className="App">
          {token ? (
            <Routes>
              <Route path="/dashboard" element={<Dashboard user={user} logout={logout} />} />
              <Route path="/groups" element={<Groups token={token} />} />
              <Route path="/expenses" element={<Expenses token={token} />} />
              <Route path="/analytics" element={<Analytics token={token} />} />
              <Route path="*" element={<Navigate to="/dashboard" />} />
            </Routes>
          ) : (
            <Routes>
              <Route path="/login" element={<Login setToken={setToken} setUser={setUser} />} />
              <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
          )}
        </div>
      </Router>
    </GoogleOAuthProvider>
  );
}

export default App;
