import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Groups from './pages/Groups';
import Expenses from './pages/Expenses';
import Analytics from './pages/Analytics';
import './App.css';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [user, setUser] = useState(null);

  useEffect(() => {
    if (token) {
      // You can decode token to get user info if needed
      setUser({ email: 'test@example.com' }); // Placeholder
    }
  }, [token]);

  const logout = () => {
    setToken('');
    setUser(null);
    localStorage.removeItem('token');
  };

  return (
    <Router>
      <div className="App">
        {token ? (
          <Routes>
            <Route path="/dashboard" element={<Dashboard user={user} logout={logout} />} />
            <Route path="/groups" element={<Groups token={token} />} />
            <Route path="/expenses" element={<Expenses token={token} />} />
            <Route path="*" element={<Navigate to="/dashboard" />} />
          </Routes>
        ) : (
          <Routes>
            <Route path="/login" element={<Login setToken={setToken} setUser={setUser} />} />
            <Route path="*" element={<Navigate to="/login" />} />
          </Routes>
        )}
      </div>
    </Router>
  );
}

export default App;
