import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE = 'http://localhost:5050/api';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('auth');

  // Simple form states
  const [email, setEmail] = useState('test@example.com');
  const [password, setPassword] = useState('password123');
  const [username, setUsername] = useState('testuser');

  const handleRequest = async (method, url, data = null) => {
    setLoading(true);
    try {
      const config = {
        method,
        url: API_BASE + url,
        ...(data && { data }),
        ...(token && { headers: { Authorization: `Bearer ${token}` } })
      };

      const res = await axios(config);
      setResponse(JSON.stringify(res.data, null, 2));

      // Save token if login/register
      if ((url.includes('/auth/login') || url.includes('/auth/register')) && res.data.token) {
        setToken(res.data.token);
        localStorage.setItem('token', res.data.token);
      }
    } catch (error) {
      setResponse(JSON.stringify(error.response?.data || error.message, null, 2));
    }
    setLoading(false);
  };

  const clearToken = () => {
    setToken('');
    localStorage.removeItem('token');
    setResponse('Logged out successfully');
  };

  const tabStyle = (isActive) => ({
    padding: '10px 20px',
    margin: '0 5px',
    backgroundColor: isActive ? '#007bff' : '#f8f9fa',
    color: isActive ? 'white' : '#333',
    border: '1px solid #ddd',
    borderRadius: '5px 5px 0 0',
    cursor: 'pointer'
  });

  const buttonStyle = {
    padding: '10px 20px',
    margin: '10px 5px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '14px'
  };

  const inputStyle = {
    width: '300px',
    padding: '10px',
    margin: '5px 0',
    borderRadius: '5px',
    border: '1px solid #ddd',
    fontSize: '14px'
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial', maxWidth: '800px', margin: '0 auto' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>🏦 ExpenseFlow Tester</h1>

      {/* Status Bar */}
      <div style={{
        padding: '15px',
        backgroundColor: token ? '#d4edda' : '#f8d7da',
        borderRadius: '5px',
        marginBottom: '20px',
        textAlign: 'center'
      }}>
        <strong>Status:</strong> {token ? '✅ Logged In' : '❌ Not Logged In'}
        {token && (
          <button
            onClick={clearToken}
            style={{ ...buttonStyle, backgroundColor: '#dc3545', marginLeft: '15px' }}
          >
            Logout
          </button>
        )}
      </div>

      {/* Tabs */}
      <div style={{ marginBottom: '20px', textAlign: 'center' }}>
        <button style={tabStyle(activeTab === 'auth')} onClick={() => setActiveTab('auth')}>
          🔐 Authentication
        </button>
        <button style={tabStyle(activeTab === 'groups')} onClick={() => setActiveTab('groups')}>
          👥 Groups
        </button>
        <button style={tabStyle(activeTab === 'expenses')} onClick={() => setActiveTab('expenses')}>
          💰 Expenses
        </button>
        <button style={tabStyle(activeTab === 'test')} onClick={() => setActiveTab('test')}>
          🧪 Testing
        </button>
      </div>

      {/* Content Area */}
      <div style={{
        border: '1px solid #ddd',
        borderRadius: '0 5px 5px 5px',
        padding: '30px',
        backgroundColor: '#fff'
      }}>

        {/* Authentication Tab */}
        {activeTab === 'auth' && (
          <div>
            <h2>🔐 Authentication</h2>

            <div style={{ marginBottom: '30px' }}>
              <h3>Quick Setup (Recommended)</h3>
              <p>Create test users with one click:</p>
              <button
                style={{ ...buttonStyle, backgroundColor: '#28a745' }}
                onClick={() => handleRequest('post', '/auth/register', {
                  username: 'testuser',
                  email: 'test@example.com',
                  password: 'password123'
                })}
              >
                1. Create Test User
              </button>
              <button
                style={{ ...buttonStyle, backgroundColor: '#28a745' }}
                onClick={() => handleRequest('post', '/auth/login', {
                  email: 'test@example.com',
                  password: 'password123'
                })}
              >
                2. Login Test User
              </button>
              <button
                style={{ ...buttonStyle, backgroundColor: '#28a745' }}
                onClick={() => handleRequest('post', '/auth/register', {
                  username: 'member1',
                  email: 'member1@example.com',
                  password: 'password123'
                })}
              >
                3. Create Member1
              </button>
            </div>

            <hr />

            <div>
              <h3>Custom Registration/Login</h3>
              <div style={{ marginBottom: '15px' }}>
                <input
                  style={inputStyle}
                  placeholder="Username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                />
              </div>
              <div style={{ marginBottom: '15px' }}>
                <input
                  style={inputStyle}
                  placeholder="Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>
              <div style={{ marginBottom: '15px' }}>
                <input
                  style={inputStyle}
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>
              <button
                style={buttonStyle}
                onClick={() => handleRequest('post', '/auth/register', { username, email, password })}
              >
                Register
              </button>
              <button
                style={buttonStyle}
                onClick={() => handleRequest('post', '/auth/login', { email, password })}
              >
                Login
              </button>
            </div>
          </div>
        )}

        {/* Groups Tab */}
        {activeTab === 'groups' && (
          <div>
            <h2>👥 Groups</h2>
            {!token && <p style={{ color: 'red' }}>⚠️ Please login first!</p>}

            <button
              style={buttonStyle}
              onClick={() => handleRequest('post', '/groups', {
                name: 'Trip to Goa',
                start_date: '2025-08-01',
                end_date: '2025-08-07'
              })}
              disabled={!token}
            >
              Create Sample Group
            </button>

            <button
              style={buttonStyle}
              onClick={() => handleRequest('get', '/groups')}
              disabled={!token}
            >
              Get My Groups
            </button>

            <div style={{ marginTop: '20px' }}>
              <p><strong>To add members:</strong></p>
              <ol>
                <li>Create a group above</li>
                <li>Copy the group ID from response</li>
                <li>Use: POST /groups/GROUP_ID/members with {`{"email": "member1@example.com"}`}</li>
              </ol>
            </div>
          </div>
        )}

        {/* Expenses Tab */}
        {activeTab === 'expenses' && (
          <div>
            <h2>💰 Expenses</h2>
            {!token && <p style={{ color: 'red' }}>⚠️ Please login first!</p>}

            <p><strong>Steps to test expenses:</strong></p>
            <ol>
              <li>Create a group first (Groups tab)</li>
              <li>Copy the group ID from the response</li>
              <li>Use the group ID below</li>
            </ol>

            <div style={{ marginTop: '20px' }}>
              <p><strong>Sample API calls:</strong></p>
              <code style={{ display: 'block', padding: '10px', backgroundColor: '#f8f9fa', margin: '10px 0' }}>
                POST /expenses<br />
                {"{"}"amount": 1000, "description": "Hotel", "category": "Accommodation", "group_id": "YOUR_GROUP_ID"{"}"}
              </code>
              <code style={{ display: 'block', padding: '10px', backgroundColor: '#f8f9fa', margin: '10px 0' }}>
                GET /expenses/group/YOUR_GROUP_ID
              </code>
            </div>
          </div>
        )}

        {/* Testing Tab */}
        {activeTab === 'test' && (
          <div>
            <h2>🧪 Testing & Debug</h2>

            <button
              style={{ ...buttonStyle, backgroundColor: '#17a2b8' }}
              onClick={() => handleRequest('get', '/test/health')}
            >
              Health Check
            </button>

            <button
              style={{ ...buttonStyle, backgroundColor: '#17a2b8' }}
              onClick={() => handleRequest('get', '/test/stats')}
            >
              Database Stats
            </button>

            <button
              style={{ ...buttonStyle, backgroundColor: '#17a2b8' }}
              onClick={() => handleRequest('get', '/test/users')}
            >
              All Users
            </button>

            <button
              style={{ ...buttonStyle, backgroundColor: '#17a2b8' }}
              onClick={() => handleRequest('get', '/test/groups')}
            >
              All Groups
            </button>
          </div>
        )}
      </div>

      {/* Response Section */}
      <div style={{
        marginTop: '30px',
        border: '1px solid #ddd',
        borderRadius: '5px',
        padding: '20px',
        backgroundColor: '#f8f9fa'
      }}>
        <h2>📋 API Response</h2>
        {loading && <p style={{ color: '#007bff' }}>⏳ Loading...</p>}
        <pre style={{
          background: '#fff',
          padding: '15px',
          borderRadius: '5px',
          overflow: 'auto',
          maxHeight: '400px',
          fontSize: '12px',
          border: '1px solid #ddd'
        }}>
          {response || 'No response yet. Click buttons above to test your API!'}
        </pre>
      </div>
    </div>
  );
}

export default App;
