import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { GoogleLogin } from '@react-oauth/google';

function ModernLogin({ setToken, setUser }) {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  // Animated background particles
  useEffect(() => {
    const createParticle = () => {
      const particle = document.createElement('div');
      particle.className = 'floating-particle';
      particle.style.left = Math.random() * 100 + '%';
      particle.style.animationDuration = (Math.random() * 3 + 2) + 's';
      particle.style.opacity = Math.random() * 0.5 + 0.1;
      document.querySelector('.particles-container')?.appendChild(particle);

      setTimeout(() => {
        particle.remove();
      }, 5000);
    };

    const interval = setInterval(createParticle, 300);
    return () => clearInterval(interval);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const url = isLogin ? '/auth/login' : '/auth/register';
      const data = isLogin
        ? { email: formData.email, password: formData.password }
        : formData;

      const response = await axios.post(`http://localhost:5055/api${url}`, data);

      if (response.data.token) {
        setToken(response.data.token);
        setUser(response.data.user);
        localStorage.setItem('token', response.data.token);
        setMessage('✨ Welcome to ExpenseFlow!');
      }
    } catch (error) {
      setMessage(error.response?.data?.message || 'Something went wrong');
    }
    setLoading(false);
  };

  const handleGoogleSuccess = async (credentialResponse) => {
    setLoading(true);
    setMessage('');
    
    try {
      console.log('🔐 Sending Google credential to backend...');
      
      // Send Google credential to your backend
      const response = await axios.post('http://localhost:5055/api/auth/google', {
        credential: credentialResponse.credential
      });

      if (response.data.token) {
        setToken(response.data.token);
        setUser(response.data.user);
        localStorage.setItem('token', response.data.token);
        setMessage('🚀 Google login successful!');
        console.log('✅ Google login successful');
      }
    } catch (error) {
      console.error('❌ Google login error:', error);
      
      // Development bypass - if Google OAuth fails, create demo login
      console.log('🔧 Using development bypass for Google login...');
      
      try {
        const bypassResponse = await axios.post('http://localhost:5055/api/auth/google-bypass', {
          email: 'demo@gmail.com',
          name: 'Demo User'
        });
        
        if (bypassResponse.data.token) {
          setToken(bypassResponse.data.token);
          setUser(bypassResponse.data.user);
          localStorage.setItem('token', bypassResponse.data.token);
          setMessage('🚀 Demo Google login successful!');
          console.log('✅ Demo Google login successful');
          return;
        }
      } catch (bypassError) {
        console.error('❌ Bypass also failed:', bypassError);
      }
      
      let errorMessage = 'Google login failed';
      
      if (error.response?.data?.error === 'GOOGLE_TOKEN_INVALID') {
        errorMessage = 'Google authentication failed. Please try again.';
      } else if (error.response?.data?.error === 'GOOGLE_PAYLOAD_INVALID') {
        errorMessage = 'Unable to get your Google account information.';
      } else if (error.response?.data?.message) {
        errorMessage = error.response.data.message;
      } else if (error.code === 'NETWORK_ERROR') {
        errorMessage = 'Network error. Please check your connection.';
      }
      
      setMessage(errorMessage);
    }
    setLoading(false);
  };

  const handleGoogleError = () => {
    setMessage('Google login was cancelled or failed');
  };

  return (
    <div className="modern-login-container">
      {/* Animated Background */}
      <div className="animated-background">
        <div className="gradient-orb orb-1"></div>
        <div className="gradient-orb orb-2"></div>
        <div className="gradient-orb orb-3"></div>
        <div className="particles-container"></div>
      </div>

      {/* Main Content */}
      <div className="login-content">
        {/* Left Side - Branding */}
        <div className="branding-section">
          <div className="brand-content">
            <div className="logo-container">
              <div className="logo-icon">💰</div>
              <h1 className="brand-title">ExpenseFlow</h1>
            </div>
            <p className="brand-subtitle">
              Smart expense tracking for modern teams
            </p>
            <div className="feature-list">
              <div className="feature-item">
                <span className="feature-icon">✨</span>
                <span>Automatic expense splitting</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">📊</span>
                <span>Real-time analytics</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">👥</span>
                <span>Group collaboration</span>
              </div>
            </div>
          </div>
        </div>

        {/* Right Side - Login Form */}
        <div className="form-section">
          <div className="form-container">
            {/* Form Header */}
            <div className="form-header">
              <h2 className="form-title">
                {isLogin ? 'Welcome Back' : 'Create Account'}
              </h2>
              <p className="form-subtitle">
                {isLogin ? 'Sign in to your account' : 'Join thousands of users'}
              </p>
            </div>

            {/* Tab Switcher */}
            <div className="tab-switcher">
              <button
                className={`tab-button ${isLogin ? 'active' : ''}`}
                onClick={() => setIsLogin(true)}
              >
                Sign In
              </button>
              <button
                className={`tab-button ${!isLogin ? 'active' : ''}`}
                onClick={() => setIsLogin(false)}
              >
                Sign Up
              </button>
              <div className={`tab-indicator ${isLogin ? 'left' : 'right'}`}></div>
            </div>

            {/* Form */}
            <form onSubmit={handleSubmit} className="modern-form">
              {!isLogin && (
                <div className="input-group">
                  <div className="input-container">
                    <input
                      type="text"
                      placeholder="Full Name"
                      value={formData.username}
                      onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                      className="modern-input"
                      required={!isLogin}
                    />
                    <div className="input-icon">👤</div>
                  </div>
                </div>
              )}

              <div className="input-group">
                <div className="input-container">
                  <input
                    type="email"
                    placeholder="Email Address"
                    value={formData.email}
                    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                    className="modern-input"
                    required
                  />
                  <div className="input-icon">📧</div>
                </div>
              </div>

              <div className="input-group">
                <div className="input-container">
                  <input
                    type={showPassword ? "text" : "password"}
                    placeholder="Password"
                    value={formData.password}
                    onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                    className="modern-input"
                    required
                  />
                  <div className="input-icon">🔒</div>
                  <button
                    type="button"
                    className="password-toggle"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? '👁️' : '👁️‍🗨️'}
                  </button>
                </div>
              </div>

              <button
                type="submit"
                disabled={loading}
                className="primary-button"
              >
                {loading ? (
                  <div className="loading-spinner"></div>
                ) : (
                  <>
                    <span>{isLogin ? 'Sign In' : 'Create Account'}</span>
                    <span className="button-arrow">→</span>
                  </>
                )}
              </button>
            </form>

            {/* Divider */}
            <div className="divider">
              <span>or</span>
            </div>

            {/* Google Login */}
            <div className="google-login-container">
              <GoogleLogin
                onSuccess={handleGoogleSuccess}
                onError={handleGoogleError}
                theme="outline"
                size="large"
                text="signin_with"
                shape="rectangular"
                logo_alignment="left"
              />
            </div>

            {/* Message */}
            {message && (
              <div className={`message ${message.includes('successful') || message.includes('Welcome') ? 'success' : 'error'}`}>
                {message}
              </div>
            )}
          </div>
        </div>
      </div>

