import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { API_ENDPOINTS } from '../utils/api';

/**
 * Modern Login/Register Component
 * 
 * Features:
 * - Beautiful animated background with floating particles
 * - Responsive design with left branding section and right form section
 * - Toggle between Login and Register modes
 * - Form validation and error handling
 * - JWT token management
 * - Password visibility toggle
 * - Loading states and success/error messages
 */
function ModernLogin({ setToken, setUser }) {
  // State for toggling between login and register mode
  const [isLogin, setIsLogin] = useState(true);
  
  // Form data state - stores username, email, password
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });
  
  // Message state for showing success/error messages
  const [message, setMessage] = useState('');
  
  // Loading state for showing spinner during API calls
  const [loading, setLoading] = useState(false);
  
  // State for password visibility toggle
  const [showPassword, setShowPassword] = useState(false);
  
  // State for resend verification
  const [showResendForm, setShowResendForm] = useState(false);
  const [resendEmail, setResendEmail] = useState('');
  const [resendLoading, setResendLoading] = useState(false);

  // Removed particle animation for clean professional look

  /**
   * Handle form submission for both login and register
   * @param {Event} e - Form submit event
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      // Determine API endpoint based on current mode (login/register)
      const url = isLogin ? '/auth/login' : '/auth/register';
      
      // Prepare data - login only needs email/password, register needs all fields
      const data = isLogin
        ? { email: formData.email, password: formData.password }
        : formData;

      // Make API call to backend using centralized API endpoints
      const apiUrl = isLogin ? API_ENDPOINTS.AUTH.LOGIN : API_ENDPOINTS.AUTH.REGISTER;
      const response = await axios.post(apiUrl, data);

      if (isLogin) {
        // Login flow - expect token in response
        if (response.data.token) {
          setToken(response.data.token); // Update parent component state
          setUser(response.data.user); // Update parent component state
          localStorage.setItem('token', response.data.token); // Persist token
          setMessage('‚ú® Welcome to ExpenseFlow!');
        }
      } else {
        // Registration flow - expect success message (no token until verified)
        if (response.data.success) {
          setMessage(`üìß ${response.data.message}`);
          // Clear form after successful registration
          setFormData({ username: '', email: '', password: '' });
        }
      }
    } catch (error) {
      // Handle specific error codes
      const errorData = error.response?.data;
      let errorMessage = errorData?.message || 'Something went wrong';
      
      // Handle specific error cases
      if (errorData?.code === 'EMAIL_NOT_VERIFIED') {
        errorMessage = `${errorMessage}`;
        setShowResendForm(true); // Show resend form for unverified emails
        setResendEmail(formData.email); // Pre-fill with login email
      } else if (errorData?.code === 'ACCOUNT_LOCKED') {
        errorMessage = `${errorMessage} Please try again later.`;
      } else if (errorData?.code === 'PENDING_VERIFICATION') {
        errorMessage = `${errorMessage} Check your spam folder if you don't see it.`;
        setShowResendForm(true); // Show resend form
        setResendEmail(formData.email); // Pre-fill with registration email
      }
      
      setMessage(errorMessage);
    }
    setLoading(false);
  };

  /**
   * Handle resend verification email
   * @param {Event} e - Form submit event
   */
  const handleResendVerification = async (e) => {
    e.preventDefault();
    if (!resendEmail) {
      setMessage('Please enter your email address');
      return;
    }

    setResendLoading(true);

    try {
      const response = await axios.post('http://localhost:5055/api/auth/resend-verification', {
        email: resendEmail
      });

      if (response.data.success) {
        setMessage('‚úÖ Verification email sent! Please check your inbox.');
        setShowResendForm(false); // Hide resend form after success
      }
    } catch (error) {
      const errorMessage = error.response?.data?.message || 'Failed to send verification email';
      setMessage(`‚ùå ${errorMessage}`);
    }
    setResendLoading(false);
  };

  return (
    <div className="modern-login-container">
      {/* Clean Professional Background */}
      <div className="animated-background"></div>

      {/* Main Content Container */}
      <div className="login-content">
        {/* Left Side - Branding Section */}
        <div className="branding-section">
          <div className="brand-content">
            {/* Logo and Brand Name */}
            <div className="logo-container">
              <div className="logo-icon">üí∞</div>
              <h1 className="brand-title">ExpenseFlow</h1>
            </div>
            
            {/* Brand Description */}
            <p className="brand-subtitle">
              Smart expense tracking for modern teams
            </p>
            
            {/* Feature List */}
            <div className="feature-list">
              <div className="feature-item">
                <span className="feature-icon">‚ú®</span>
                <span>Automatic expense splitting</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üìä</span>
                <span>Real-time analytics</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üë•</span>
                <span>Group collaboration</span>
              </div>
            </div>
          </div>
        </div>

        {/* Right Side - Login/Register Form */}
        <div className="form-section">
          <div className="form-container">
            {/* Form Header */}
            <div className="form-header">
              <h2 className="form-title">
                {isLogin ? 'Welcome Back' : 'Create Account'}
              </h2>
              <p className="form-subtitle">
                {isLogin ? 'Sign in to your account' : 'Join thousands of users'}
              </p>
            </div>

            {/* Tab Switcher for Login/Register */}
            <div className="tab-switcher">
              <button
                className={`tab-button ${isLogin ? 'active' : ''}`}
                onClick={() => setIsLogin(true)}
              >
                Sign In
              </button>
              <button
                className={`tab-button ${!isLogin ? 'active' : ''}`}
                onClick={() => setIsLogin(false)}
              >
                Sign Up
              </button>
              {/* Animated indicator that slides between tabs */}
              <div className={`tab-indicator ${isLogin ? 'left' : 'right'}`}></div>
            </div>

            {/* Main Form */}
            <form onSubmit={handleSubmit} className="modern-form">
              {/* Username field - only shown during registration */}
              {!isLogin && (
                <div className="input-group">
                  <div className="input-container">
                    <input
                      type="text"
                      placeholder="Full Name"
                      value={formData.username}
                      onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                      className="modern-input"
                      required={!isLogin}
                    />
                    <div className="input-icon">üë§</div>
                  </div>
                </div>
              )}

              {/* Email field - required for both login and register */}
              <div className="input-group">
                <div className="input-container">
                  <input
                    type="email"
                    placeholder="Email Address"
                    value={formData.email}
                    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                    className="modern-input"
                    required
                  />
                  <div className="input-icon">üìß</div>
                </div>
              </div>

              {/* Password field with visibility toggle */}
              <div className="input-group">
                <div className="input-container">
                  <input
                    type={showPassword ? "text" : "password"}
                    placeholder="Password"
                    value={formData.password}
                    onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                    className="modern-input"
                    required
                  />
                  <div className="input-icon">üîí</div>
                  {/* Password visibility toggle button */}
                  <button
                    type="button"
                    className="password-toggle"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                  </button>
                </div>
              </div>

              {/* Submit button with loading state */}
              <button
                type="submit"
                disabled={loading}
                className="primary-button"
              >
                {loading ? (
                  <div className="loading-spinner"></div>
                ) : (
                  <>
                    <span>{isLogin ? 'Sign In' : 'Create Account'}</span>
                    <span className="button-arrow">‚Üí</span>
                  </>
                )}
              </button>
            </form>

            {/* Success/Error Messages */}
            {message && (
              <div className={`message ${message.includes('Welcome') || message.includes('üìß') ? 'success' : 'error'}`}>
                {message}
              </div>
            )}

            {/* Resend Verification Form */}
            {showResendForm && (
              <div className="resend-verification-section">
                <h3>Resend Verification Email</h3>
                <form onSubmit={handleResendVerification} className="resend-form">
                  <div className="input-group">
                    <div className="input-container">
                      <input
                        type="email"
                        placeholder="Enter your email address"
                        value={resendEmail}
                        onChange={(e) => setResendEmail(e.target.value)}
                        className="modern-input"
                        required
                      />
                      <div className="input-icon">üìß</div>
                    </div>
                  </div>
                  <button
                    type="submit"
                    disabled={resendLoading}
                    className="resend-button"
                  >
                    {resendLoading ? (
                      <div className="loading-spinner"></div>
                    ) : (
                      'Resend Verification Email'
                    )}
                  </button>
                </form>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* CSS Styles - All styling for the component */}
      <style jsx>{`
