/**
 * API Configuration Utility
 * 
 * Centralized configuration for API endpoints and base URL
 * Supports environment-based configuration for development and production
 */

// Get API base URL from environment variable or fallback to localhost
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5055';

/**
 * API endpoints configuration
 */
export const API_ENDPOINTS = {
  // Authentication endpoints
  AUTH: {
    REGISTER: `${API_BASE_URL}/api/auth/register`,
    LOGIN: `${API_BASE_URL}/api/auth/login`,
    VERIFY_EMAIL: `${API_BASE_URL}/api/auth/verify-email`,
    RESEND_VERIFICATION: `${API_BASE_URL}/api/auth/resend-verification`,
  },
  
  // Test endpoints
  TEST: {
    HEALTH: `${API_BASE_URL}/api/test/health`,
    CURRENT_USER: `${API_BASE_URL}/api/test/current-user`,
    TEST_EXPENSE: `${API_BASE_URL}/api/test/test-expense`,
  },
  
  // Groups endpoints
  GROUPS: {
    BASE: `${API_BASE_URL}/api/groups`,
    MEMBERS: (groupId) => `${API_BASE_URL}/api/groups/${groupId}/members`,
  },
  
  // Expenses endpoints
  EXPENSES: {
    BASE: `${API_BASE_URL}/api/expenses`,
    BY_GROUP: (groupId) => `${API_BASE_URL}/api/expenses/group/${groupId}`,
  },
  
  // Analytics endpoints
  ANALYTICS: {
    BY_GROUP: (groupId) => `${API_BASE_URL}/api/analytics/group/${groupId}`,
  },
  
  // Settlement endpoints
  SETTLEMENTS: {
    BASE: `${API_BASE_URL}/api/settlements`,
    BALANCES: (groupId) => `${API_BASE_URL}/api/settlements/balances/${groupId}`,
    BY_GROUP: (groupId) => `${API_BASE_URL}/api/settlements/group/${groupId}`,
    DELETE: (settlementId) => `${API_BASE_URL}/api/settlements/${settlementId}`,
  },
  
  // Khata Book endpoints
  KHATABOOK: {
    BASE: `${API_BASE_URL}/api/khatabook`,
  },
  
  // Personal Khata endpoints
  PERSONAL_KHATA: {
    CUSTOMERS: `${API_BASE_URL}/api/personal-khata/customers`,
    CUSTOMERS_SEARCH: (search) => `${API_BASE_URL}/api/personal-khata/customers?search=${encodeURIComponent(search)}`,
    SUMMARY: `${API_BASE_URL}/api/personal-khata/summary`,
    TRANSACTIONS: `${API_BASE_URL}/api/personal-khata/transactions`,
    CUSTOMER_TRANSACTIONS: (customerId) => `${API_BASE_URL}/api/personal-khata/customers/${customerId}/transactions`,
    DELETE_CUSTOMER: (customerId) => `${API_BASE_URL}/api/personal-khata/customers/${customerId}`,
  }
};

/**
 * Helper function to build API URL
 * @param {string} endpoint - API endpoint path
 * @returns {string} Complete API URL
 */
export const buildApiUrl = (endpoint) => {
  return `${API_BASE_URL}${endpoint}`;
};

/**
 * Get API base URL
 * @returns {string} API base URL
 */
export const getApiBaseUrl = () => {
  return API_BASE_URL;
};

/**
 * Check if running in development mode
 * @returns {boolean} True if development
 */
export const isDevelopment = () => {
  return process.env.NODE_ENV === 'development';
};

/**
 * Default axios configuration
 */
export const defaultAxiosConfig = {
  baseURL: API_BASE_URL,
  timeout: 10000, // 10 seconds timeout
  headers: {
    'Content-Type': 'application/json',
  },
};

// Export API base URL as default
export default API_BASE_URL;
