import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE = 'http://localhost:5050/api';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  // Form states
  const [authForm, setAuthForm] = useState({ username: '', email: '', password: '' });
  const [groupForm, setGroupForm] = useState({ name: '', start_date: '', end_date: '' });
  const [expenseForm, setExpenseForm] = useState({ 
    amount: '', description: '', category: 'Food', customCategory: '', group_id: '' 
  });
  const [memberForm, setMemberForm] = useState({ group_id: '', email: '' });
  const [groupId, setGroupId] = useState('');

  const api = axios.create({
    baseURL: API_BASE,
    headers: token ? { Authorization: `Bearer ${token}` } : {}
  });

  const handleRequest = async (method, url, data = null) => {
    setLoading(true);
    try {
      const config = {
        method,
        url,
        ...(data && { data })
      };

      if (token) {
        config.headers = { Authorization: `Bearer ${token}` };
      }

      const res = await api(config);
      setResponse(JSON.stringify(res.data, null, 2));

      // Save token if login/register
      if ((url.includes('/auth/login') || url.includes('/auth/register')) && res.data.token) {
        setToken(res.data.token);
        localStorage.setItem('token', res.data.token);
      }
    } catch (error) {
      setResponse(JSON.stringify(error.response?.data || error.message, null, 2));
    }
    setLoading(false);
  };

  const clearToken = () => {
    setToken('');
    localStorage.removeItem('token');
    setResponse('Token cleared');
  };

  const inputStyle = {
    width: '100%',
    padding: '8px',
    margin: '5px 0',
    borderRadius: '4px',
    border: '1px solid #ddd'
  };

  const buttonStyle = {
    padding: '10px 15px',
    margin: '5px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  };

  const sectionStyle = {
    border: '1px solid #ddd',
    borderRadius: '8px',
    padding: '15px',
    margin: '10px 0'
  };

  return (
    <div className="App" style={{ padding: '20px', fontFamily: 'Arial', maxWidth: '1200px', margin: '0 auto' }}>
      <h1>üè¶ ExpenseFlow API Tester</h1>

      <div style={{ marginBottom: '20px', padding: '10px', backgroundColor: '#f8f9fa', borderRadius: '5px' }}>
        <strong>Status:</strong> {token ? '‚úÖ Logged in' : '‚ùå Not logged in'}
        {token && <button onClick={clearToken} style={{...buttonStyle, marginLeft: '10px', backgroundColor: '#dc3545'}}>Logout</button>}
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>

        {/* Auth Section */}
        <div style={sectionStyle}>
          <h2>üîê Authentication</h2>

          <h4>Register User</h4>
          <input 
            style={inputStyle}
            placeholder="Username" 
            value={authForm.username}
            onChange={(e) => setAuthForm({...authForm, username: e.target.value})}
          />
          <input 
            style={inputStyle}
            placeholder="Email" 
            value={authForm.email}
            onChange={(e) => setAuthForm({...authForm, email: e.target.value})}
          />
          <input 
            style={inputStyle}
            type="password"
            placeholder="Password" 
            value={authForm.password}
            onChange={(e) => setAuthForm({...authForm, password: e.target.value})}
          />
          <button 
            style={buttonStyle}
            onClick={() => handleRequest('post', '/auth/register', authForm)}
          >
            Register
          </button>

          <h4>Login</h4>
          <button 
            style={buttonStyle}
            onClick={() => handleRequest('post', '/auth/login', {
              email: authForm.email,
              password: authForm.password
            })}
          >
            Login
          </button>

          <h4>Quick Test Users</h4>
          <button 
            style={{...buttonStyle, backgroundColor: '#28a745'}}
            onClick={() => {
              setAuthForm({username: 'testuser', email: 'test@example.com', password: 'password123'});
              handleRequest('post', '/auth/register', {username: 'testuser', email: 'test@example.com', password: 'password123'});
            }}
          >
            Create Test User
          </button>
          <button 
            style={{...buttonStyle, backgroundColor: '#28a745'}}
            onClick={() => {
              setAuthForm({username: 'member1', email: 'member1@example.com', password: 'password123'});
              handleRequest('post', '/auth/register', {username: 'member1', email: 'member1@example.com', password: 'password123'});
            }}
          >
            Create Member1
          </button>
        </div>

        {/* Groups Section */}
        <div style={sectionStyle}>
          <h2>üë• Groups</h2>

          <h4>Create Group</h4>
          <input 
            style={inputStyle}
            placeholder="Group Name" 
            value={groupForm.name}
            onChange={(e) => setGroupForm({...groupForm, name: e.target.value})}
          />
          <input 
            style={inputStyle}
            type="date"
            placeholder="Start Date" 
            value={groupForm.start_date}
            onChange={(e) => setGroupForm({...groupForm, start_date: e.target.value})}
          />
          <input 
            style={inputStyle}
            type="date"
            placeholder="End Date" 
            value={groupForm.end_date}
            onChange={(e) => setGroupForm({...groupForm, end_date: e.target.value})}
          />
          <button 
            style={buttonStyle}
            onClick={() => handleRequest('post', '/groups', groupForm)}
          >
            Create Group
          </button>

          <h4>Group Actions</h4>
          <button 
            style={buttonStyle}
            onClick={() => handleRequest('get', '/groups')}
          >
            Get My Groups
          </button>

          <input 
            style={inputStyle}
            placeholder="Group ID for details" 
            value={groupId}
            onChange={(e) => setGroupId(e.target.value)}
          />
          <button 
            style={buttonStyle}
            onClick={() => handleRequest('get', `/groups/${groupId}`)}
            disabled={!groupId}
          >
            Get Group Details
          </button>

          <h4>Add Member to Group</h4>
          <input 
            style={inputStyle}
            placeholder="Group ID" 
            value={memberForm.group_id}
            onChange={(e) => setMemberForm({...memberForm, group_id: e.target.value})}
          />
          <input 
            style={inputStyle}
            placeholder="Member Email" 
            value={memberForm.email}
            onChange={(e) => setMemberForm({...memberForm, email: e.target.value})}
          />
          <button 
            style={buttonStyle}
            onClick={() => handleRequest('post', `/groups/${memberForm.group_id}/members`, {email: memberForm.email})}
            disabled={!memberForm.group_id || !memberForm.email}
          >
            Add Member
          </button>
        </div>

        {/* Expenses Section */}
        <div style={sectionStyle}>
          <h2>üí∞ Expenses</h2>

          <h4>Create Group Expense</h4>
          <input 
            style={inputStyle}
            type="number"
            placeholder="Amount" 
            value={expenseForm.amount}
            onChange={(e) => setExpenseForm({...expenseForm, amount: e.target.value})}
          />
          <input 
            style={inputStyle}
            placeholder="Description" 
            value={expenseForm.description}
            onChange={(e) => setExpenseForm({...expenseForm, description: e.target.value})}
          />
          <select 
            style={inputStyle}
            value={expenseForm.category}
            onChange={(e) => setExpenseForm({...expenseForm, category: e.target.value})}
          >
            <option value="Food">Food</option>
            <option value="Transport">Transport</option>
            <option value="Accommodation">Accommodation</option>
            <option value="Entertainment">Entertainment</option>
            <option value="Others">Others</option>
          </select>
          {expenseForm.category === 'Others' && (
            <input 
              style={inputStyle}
              placeholder="Custom Category" 
              value={expenseForm.customCategory}
              onChange={(e) => setExpenseForm({...expenseForm, customCategory: e.target.value})}
            />
          )}
          <input 
            style={inputStyle}
            placeholder="Group ID" 
            value={expenseForm.group_id}
            onChange={(e) => setExpenseForm({...expenseForm, group_id: e.target.value})}
          />
          <button 
            style={buttonStyle}
            onClick={() => handleRequest('post', '/expenses', expenseForm)}
            disabled={!expenseForm.amount || !expenseForm.description || !expenseForm.group_id}
          >
            Create Expense
          </button>

          <h4>Get Group Expenses</h4>
          <button 
            style={buttonStyle}
            onClick={() => handleRequest('get', `/expenses/group/${expenseForm.group_id}`)}
            disabled={!expenseForm.group_id}
          >
            Get Group Expenses
          </button>
        </div>

        {/* Test Section */}
        <div style={sectionStyle}>
          <h2>üß™ Testing & Debug</h2>
          <button 
            style={{...buttonStyle, backgroundColor: '#17a2b8'}}
            onClick={() => handleRequest('get', '/test/health')}
          >
            Health Check
          </button>
          <button 
            style={{...buttonStyle, backgroundColor: '#17a2b8'}}
            onClick={() => handleRequest('get', '/test/stats')}
          >
            Database Stats
          </button>
          <button 
            style={{...buttonStyle, backgroundColor: '#17a2b8'}}
            onClick={() => handleRequest('get', '/test/users')}
          >
            All Users
          </button>
          <button 
            style={{...buttonStyle, backgroundColor: '#17a2b8'}}
            onClick={() => handleRequest('get', '/test/groups')}
          >
            All Groups
          </button>
        </div>
      </div>

      {/* Response Section */}
      <div style={{...sectionStyle, marginTop: '30px'}}>
        <h2>üìã API Response</h2>
        {loading && <p style={{color: '#007bff'}}>‚è≥ Loading...</p>}
        <pre style={{ 
          background: '#f8f9fa', 
          padding: '15px', 
          borderRadius: '5px',
          overflow: 'auto',
          maxHeight: '500px',
          fontSize: '12px',
          border: '1px solid #dee2e6'
        }}>
          {response || 'No response yet. Use the forms above to test your API!'}
        </pre>
      </div>
    </div>
  );
}

export default App;
