import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  // Input states
  const [groupId, setGroupId] = useState('');
  const [expenseAmount, setExpenseAmount] = useState('1000');
  const [expenseDescription, setExpenseDescription] = useState('Hotel booking');
  const [memberEmail, setMemberEmail] = useState('member1@example.com');

  const makeRequest = async (method, url, data = null) => {
    setLoading(true);
    try {
      const config = {
        method,
        url: `http://localhost:5050/api${url}`,
        headers: token ? { Authorization: `Bearer ${token}` } : {},
        ...(data && { data })
      };

      const res = await axios(config);
      setResponse(JSON.stringify(res.data, null, 2));

      // Save token if login/register
      if (url.includes('/auth/') && res.data.token) {
        setToken(res.data.token);
        localStorage.setItem('token', res.data.token);
      }
    } catch (error) {
      setResponse(JSON.stringify(error.response?.data || error.message, null, 2));
    }
    setLoading(false);
  };

  const logout = () => {
    setToken('');
    localStorage.removeItem('token');
    setResponse('Logged out');
  };

  const buttonStyle = {
    padding: '10px 15px',
    margin: '5px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h1>ExpenseFlow Tester</h1>

      <div style={{
        padding: '10px',
        backgroundColor: token ? '#d4edda' : '#f8d7da',
        marginBottom: '20px',
        borderRadius: '5px'
      }}>
        Status: {token ? '‚úÖ Logged In' : '‚ùå Not Logged In'}
        {token && <button onClick={logout} style={{ ...buttonStyle, marginLeft: '10px' }}>Logout</button>}
      </div>

      <div style={{ marginBottom: '30px' }}>
        <h2>üîê Authentication</h2>
        <button
          style={{ ...buttonStyle, backgroundColor: '#28a745' }}
          onClick={() => makeRequest('post', '/auth/register', {
            username: 'testuser',
            email: 'test@example.com',
            password: 'password123'
          })}
        >
          Create Test User
        </button>

        <button
          style={{ ...buttonStyle, backgroundColor: '#28a745' }}
          onClick={() => makeRequest('post', '/auth/login', {
            email: 'test@example.com',
            password: 'password123'
          })}
        >
          Login
        </button>

        <button
          style={{ ...buttonStyle, backgroundColor: '#28a745' }}
          onClick={() => makeRequest('post', '/auth/register', {
            username: 'member1',
            email: 'member1@example.com',
            password: 'password123'
          })}
        >
          Create Member1
        </button>
      </div>

      <div style={{ marginBottom: '30px' }}>
        <h2>üë• Groups</h2>
        <button
          style={buttonStyle}
          onClick={() => makeRequest('post', '/groups', {
            name: 'Trip to Goa',
            start_date: '2025-08-01',
            end_date: '2025-08-07'
          })}
          disabled={!token}
        >
          Create Group
        </button>

        <button
          style={buttonStyle}
          onClick={() => makeRequest('get', '/groups')}
          disabled={!token}
        >
          Get My Groups
        </button>

        <div style={{ marginTop: '15px' }}>
          <h4>Add Member to Group</h4>
          <input
            style={{ padding: '8px', margin: '5px', width: '200px' }}
            placeholder="Group ID"
            value={groupId}
            onChange={(e) => setGroupId(e.target.value)}
          />
          <input
            style={{ padding: '8px', margin: '5px', width: '200px' }}
            placeholder="Member Email"
            value={memberEmail}
            onChange={(e) => setMemberEmail(e.target.value)}
          />
          <button
            style={buttonStyle}
            onClick={() => makeRequest('post', `/groups/${groupId}/members`, { email: memberEmail })}
            disabled={!token || !groupId || !memberEmail}
          >
            Add Member
          </button>
        </div>
      </div>

      <div style={{ marginBottom: '30px' }}>
        <h2>üí∞ Expenses</h2>

        <div style={{ marginBottom: '15px' }}>
          <h4>Create Group Expense</h4>
          <input
            style={{ padding: '8px', margin: '5px', width: '200px' }}
            placeholder="Group ID"
            value={groupId}
            onChange={(e) => setGroupId(e.target.value)}
          />
          <input
            style={{ padding: '8px', margin: '5px', width: '150px' }}
            placeholder="Amount"
            value={expenseAmount}
            onChange={(e) => setExpenseAmount(e.target.value)}
          />
          <input
            style={{ padding: '8px', margin: '5px', width: '200px' }}
            placeholder="Description"
            value={expenseDescription}
            onChange={(e) => setExpenseDescription(e.target.value)}
          />
          <button
            style={buttonStyle}
            onClick={() => makeRequest('post', '/expenses', {
              amount: parseFloat(expenseAmount),
              description: expenseDescription,
              category: 'Accommodation',
              group_id: groupId
            })}
            disabled={!token || !groupId || !expenseAmount || !expenseDescription}
          >
            Create Expense
          </button>
        </div>

        <div>
          <h4>Get Group Expenses</h4>
          <button
            style={buttonStyle}
            onClick={() => makeRequest('get', `/expenses/group/${groupId}`)}
            disabled={!token || !groupId}
          >
            Get Expenses for Group
          </button>
        </div>
      </div>

      <div style={{ marginBottom: '30px' }}>
        <h2>üß™ Testing</h2>
        <button
          style={{ ...buttonStyle, backgroundColor: '#17a2b8' }}
          onClick={() => makeRequest('get', '/test/health')}
        >
          Health Check
        </button>

        <button
          style={{ ...buttonStyle, backgroundColor: '#17a2b8' }}
          onClick={() => makeRequest('get', '/test/stats')}
        >
          Database Stats
        </button>

        <button
          style={{ ...buttonStyle, backgroundColor: '#17a2b8' }}
          onClick={() => makeRequest('get', '/test/users')}
        >
          All Users
        </button>

        <button
          style={{ ...buttonStyle, backgroundColor: '#17a2b8' }}
          onClick={() => makeRequest('get', '/test/groups')}
        >
          All Groups
        </button>
      </div>

      <div style={{
        border: '1px solid #ddd',
        padding: '15px',
        borderRadius: '5px',
        backgroundColor: '#f8f9fa'
      }}>
        <h2>Response</h2>
        {loading && <p>Loading...</p>}
        <pre style={{
          background: 'white',
          padding: '10px',
          borderRadius: '3px',
          overflow: 'auto',
          maxHeight: '400px',
          fontSize: '12px'
        }}>
          {response || 'Click buttons above to test API'}
        </pre>
      </div>
    </div>
  );
}

export default App;
