import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

/**
 * Email Verification Page
 * 
 * Handles email verification when user clicks verification link
 * Also provides UI for resending verification emails
 */
function EmailVerification() {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const [verificationStatus, setVerificationStatus] = useState('verifying'); // verifying, success, error
  const [message, setMessage] = useState('');
  const [resendEmail, setResendEmail] = useState('');
  const [resendLoading, setResendLoading] = useState(false);
  const [resendMessage, setResendMessage] = useState('');

  // Get token from URL parameters
  const token = searchParams.get('token');

  useEffect(() => {
    if (token) {
      verifyEmail(token);
    } else {
      setVerificationStatus('error');
      setMessage('Invalid verification link. Please check your email for the correct link.');
    }
  }, [token]);

  /**
   * Verify email with token from URL
   */
  const verifyEmail = async (verificationToken) => {
    try {
      const response = await axios.get(`http://localhost:5055/api/auth/verify-email?token=${verificationToken}`);
      
      if (response.data.success) {
        setVerificationStatus('success');
        setMessage(response.data.message);
        
        // Redirect to login after 3 seconds
        setTimeout(() => {
          navigate('/login');
        }, 3000);
      }
    } catch (error) {
      setVerificationStatus('error');
      const errorMessage = error.response?.data?.message || 'Verification failed. Please try again.';
      setMessage(errorMessage);
    }
  };

  /**
   * Resend verification email
   */
  const handleResendVerification = async (e) => {
    e.preventDefault();
    if (!resendEmail) {
      setResendMessage('Please enter your email address');
      return;
    }

    setResendLoading(true);
    setResendMessage('');

    try {
      const response = await axios.post('http://localhost:5055/api/auth/resend-verification', {
        email: resendEmail
      });

      if (response.data.success) {
        setResendMessage('✅ Verification email sent! Please check your inbox.');
      }
    } catch (error) {
      const errorMessage = error.response?.data?.message || 'Failed to send verification email';
      setResendMessage(`❌ ${errorMessage}`);
    }
    setResendLoading(false);
  };

  return (
    <div className="verification-container">
      <div className="verification-card">
        {/* Header */}
        <div className="verification-header">
          <div className="logo">💰 ExpenseFlow</div>
          <h1>Email Verification</h1>
        </div>

        {/* Verification Status */}
        <div className="verification-content">
          {verificationStatus === 'verifying' && (
            <div className="status-section">
              <div className="loading-spinner"></div>
              <h2>Verifying your email...</h2>
              <p>Please wait while we verify your email address.</p>
            </div>
          )}

          {verificationStatus === 'success' && (
            <div className="status-section success">
              <div className="success-icon">✅</div>
              <h2>Email Verified Successfully!</h2>
              <p>{message}</p>
              <p className="redirect-message">Redirecting to login page in 3 seconds...</p>
              <button 
                onClick={() => navigate('/login')}
                className="primary-button"
              >
                Go to Login
              </button>
            </div>
          )}

          {verificationStatus === 'error' && (
            <div className="status-section error">
              <div className="error-icon">❌</div>
              <h2>Verification Failed</h2>
              <p>{message}</p>
              
              {/* Resend Verification Form */}
              <div className="resend-section">
                <h3>Need a new verification email?</h3>
                <form onSubmit={handleResendVerification} className="resend-form">
                  <div className="input-group">
                    <input
                      type="email"
                      placeholder="Enter your email address"
                      value={resendEmail}
                      onChange={(e) => setResendEmail(e.target.value)}
                      className="resend-input"
                      required
                    />
                  </div>
                  <button
                    type="submit"
                    disabled={resendLoading}
                    className="resend-button"
                  >
                    {resendLoading ? 'Sending...' : 'Resend Verification Email'}
                  </button>
                </form>
                {resendMessage && (
                  <div className={`resend-message ${resendMessage.includes('✅') ? 'success' : 'error'}`}>
                    {resendMessage}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="verification-footer">
          <p>Having trouble? Contact support or try registering again.</p>
          <button 
            onClick={() => navigate('/login')}
            className="link-button"
          >
            Back to Login
          </button>
        </div>
      </div>

