import React from 'react';
import { useNavigate } from 'react-router-dom';

function Dashboard({ user, logout }) {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen" style={{ background: '#f8f9fa' }}>
      {/* Header */}
      <header className="nav-header">
        <div className="nav-container">
          <div className="nav-brand">
            <span className="page-title-icon">üè¶</span>
            <span>ExpenseFlow</span>
          </div>
          <div className="flex items-center gap-4">
            <span className="text-secondary">Welcome, {user?.username || 'User'}!</span>
            <button onClick={logout} className="btn btn-danger btn-sm">
              Logout
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main style={{ 
        maxWidth: '1200px', 
        margin: '0 auto', 
        padding: '40px 20px' 
      }}>
        {/* Hero Section */}
        <div style={{ 
          textAlign: 'center', 
          marginBottom: '50px',
          padding: '40px 20px',
          backgroundColor: 'white',
          borderRadius: '15px',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
        }}>
          <h1 style={{ 
            fontSize: '2.5rem', 
            fontWeight: 'bold', 
            color: '#333',
            marginBottom: '15px',
            margin: 0
          }}>
            Welcome back, {user?.username || 'User'}! üëã
          </h1>
          <p style={{ 
            fontSize: '1.2rem', 
            color: '#666',
            maxWidth: '600px',
            margin: '15px auto 0 auto',
            lineHeight: '1.6'
          }}>
            Ready to manage your expenses? Split bills, track spending, and settle up with friends effortlessly.
          </p>
        </div>

        {/* Dashboard Cards Grid */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '25px',
          marginBottom: '40px'
        }}>
          {/* Groups Card */}
          <div style={{
